	{
		$this_script = str_replace( '/'.CP_DIRECTORY.'/index.php', '/uploads', $this_script );
 		$r['conf_description'] = str_replace( "<!--SUGGEST-->", "<br />IP.Board определил следующий путь к директории загрузок: <strong>".$this_script . '</strong>', $r['conf_description'] );
	}
}

if( $show == 1 )
{
	$this_host   = getenv( 'SERVER_NAME' );
	$this_script = getenv( 'SCRIPT_NAME' );
	$this_script = str_replace( "&#092;&#092;&#092;&#092;", "/", $this_script );

	if( $this_host AND $this_script )
	{
		$this_script = str_replace( '/'.CP_DIRECTORY.'/index.php', '/uploads', $this_script );
		$r['conf_description'] = str_replace( "<!--SUGGEST-->", "<br />IP.Board определил URL к директории загрузок так: <strong>http://".$this_host.$this_script . '</strong>', $r['conf_description'] );
	}
}

