setting.conf_extra:
0=0: Отключен&#13;
1=1: Выводить уровень загрузки сервера, время генерации страницы и число запросов&#13;
2=2: Выводить информацию уровня 1 плюс данные из GET и POST&#13;
3=3: Выводить информацию уровня 2 плюс запросы к базе данных

0=Не записывать никаких общих ошибок&#13;
1=Записывать абсолютно все ошибки&#13;
2=Записывать все ошибки 1 уровня и выше&#13;
3=Записывать все ошибки 2 уровня и выше&#13;
4=Записывать все ошибки 3 уровня и выше&#13;
5=Записывать все ошибки 4 уровня и выше&#13;
6=Записывать все ошибки 5 уровня

0=Не уведомлять вообще&#13;
1=Уведомлять обо всех ошибках&#13;
2=Уведомлять об ошибках 1 уровня и выше&#13;
3=Уведомлять об ошибках 2 уровня и выше&#13;
4=Уведомлять об ошибках 3 уровня и выше&#13;
5=Уведомлять об ошибках 4 уровня и выше&#13;
6=Уведомлять об ошибках 5 уровня

setting.conf_evalphp:
if ( $save == 1)
{
	if ( is_array($_POST['vnp_block_forums']) )
	{
		$_POST['vnp_block_forums'] = implode(",",$_POST['vnp_block_forums']);
	}
	else
	{
		$_POST['vnp_block_forums'] = "";
	}

	$key = 'vnp_block_forums';
}
if ( $show == 1 )
{
$key = 'vnp_block_forums[]';
}

if ( $show == 1 AND ! $this->settings['use_friendly_urls'] )
{
	$r['conf_description'] .= "<div style='color:red'>Внимание: ЧПУ не автивировано. Вам необходимо изменить 'conf_global.php' и добавить в конец файла строку: <kbd>&#092;$INFO&#092;['use_friendly_urls'&#092;]            = '1';</kdb></div>";
}

if ( $show == 1 )
{
	ipsRegistry::getClass('class_localization')->loadLanguageFile( array( 'public_usercp' ), 'core' );

	foreach( ipsRegistry::getClass('class_localization')->words as $off => $words )
	{
		if (preg_match("/^time_(-?[&#092;d&#092;.]+)$/", $off, $match))
		{
			$dropdown[ $match[1] ] = array( $match[1], $words );
		}
	}

	ksort($dropdown);
}

